<?php/**Helper Events Facebook */class modPgrFbEventsHelper{    public static function getEvents($params) {        $app_id = $params->get('app_id');        $app_secret = $params->get('app_secret');        $uid = $params->get('uid');        try {            $facebook = new Facebook(array(                'appId'  => $app_id,                'secret' => $app_secret,                'cookie' => true, // enable optional cookie support          ));        } catch (Exception $e) {            require_once JPATH_ROOT.'/modules/mod_pgr_fbevents/assets/libraries/fbsdk/facebook.php';            $facebook = new Facebook(array(                    'appId'  => $app_id,                    'secret' => $app_secret,                    'cookie' => true, // enable optional cookie support            ));          }        $fql = "SELECT name, pic, eid, start_time, end_time, location, description, update_time FROM event WHERE eid IN ( SELECT eid FROM event_member WHERE uid = $uid AND start_time>0) ORDER BY start_time desc LIMIT 3";        $param  =   array(                'method'    => 'fql.query',                'query'     => $fql,                'callback'  => ''        );        try {            $events = $facebook->api($param);        } catch (FacebookApiException $e) {            error_log($e->getMessage());        }        return $events;    }}